self:RegisterEvent("COMBAT_LOG_EVENT_UNFILTERED");

Scripts.OnClick:
if self:GetChecked() then
	CEPGP_context_popup_REMOVE_check:SetChecked(nil);
else
	self:SetChecked(1);
end

